function Login(){var n=[];n.push(new RequestData("Username",$("#username").val()));n.push(new RequestData("Password",$("#password").val()));AjaxRequest("/WebControl.ch?Command=Login",function(n){n.Status==1?location.href="":$("#loginMessage").text(n.Message)},n)}function Logout(){AjaxRequest("/WebControl.ch?Command=Logout",function(n){n.Status==1?location.href="":$("#loginMessage").text(n.Message)})}$(function(){if($("#loginForm").length>0)$("#loginForm").on("submit",function(n){n.preventDefault();Login()})});